import React from "react";
import { Link } from "react-router-dom";
import useCategory from "../hooks/useCategory";
import Layout from "../components/Layout/Layout";
import { FaCarAlt } from "react-icons/fa";
import "../styles/CategoryStyles.css";

const Categories = () => {
  const categories = useCategory();

  return (
    <Layout title={"Vehicle Categories"}>
      <div className="categories-page">
        <div className="categories-header">
          <h1>Vehicle Categories</h1>
          <p>Browse our extensive collection of vehicles by category</p>
        </div>

        <div className="categories-grid">
          {categories.map((category) => (
            <Link 
              to={`/category/${category.slug}`} 
              className="category-card" 
              key={category._id}
            >
              <div className="category-icon">
                <FaCarAlt />
              </div>
              <div className="category-content">
                <h3>{category.name}</h3>
                <span className="arrow">â†’</span>
              </div>
            </Link>
          ))}
        </div>

        <div className="categories-info">
          <div className="info-card">
            <h4>Why Choose Our Categories?</h4>
            <ul>
              <li>Wide range of vehicles for every need</li>
              <li>Competitive pricing across categories</li>
              <li>Well-maintained and serviced vehicles</li>
              <li>Flexible rental terms</li>
            </ul>
          </div>
        </div>
      </div>
    </Layout>
  );
};

export default Categories;
