import React, { useEffect } from "react";
import Layout from "../components/Layout/Layout";
import { useCart } from "../context/cart";
import { useAuth } from "../context/auth";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import "../styles/CartStyles.css";
import { toast } from "react-hot-toast";

const CartPage = () => {
  const [auth] = useAuth();
  const [cart, setCart] = useCart();
  const navigate = useNavigate();

  // Load Razorpay script on component mount
  useEffect(() => {
    const loadRazorpayScript = async () => {
      return new Promise((resolve) => {
        const script = document.createElement("script");
        script.src = "https://checkout.razorpay.com/v1/checkout.js";
        script.async = true;
        script.onload = () => resolve(true);
        script.onerror = () => {
          script.remove();
          resolve(false);
        };
        document.body.appendChild(script);
      });
    };
    loadRazorpayScript();
  }, []);

  // Function to calculate total price
  const totalPrice = () => {
    try {
      const total = cart.reduce((sum, item) => sum + Number(item.price), 0);
      return total.toLocaleString("en-IN", {
        style: "currency",
        currency: "INR",
      });
    } catch (error) {
      console.error("Error calculating total:", error);
      return "‚Çπ0";
    }
  };

  // Function to remove an item from cart
  const removeCartItem = (pid) => {
    try {
      const updatedCart = cart.filter((item) => item._id !== pid);
      setCart(updatedCart);
      localStorage.setItem("cart", JSON.stringify(updatedCart));
      toast.success("Item removed from cart");
    } catch (error) {
      toast.error("Could not remove item");
    }
  };

  // Handle payment process
  const handlePayment = async () => {
    if (!window.Razorpay) {
      toast.error("Payment gateway is loading. Please try again in a moment.");
      return;
    }

    try {
      // Basic validations
      if (!auth?.token) {
        toast.error("Please login to proceed");
        navigate("/login");
        return;
      }

      if (!cart.length) {
        toast.error("Your cart is empty");
        return;
      }

      if (!auth?.user?.address) {
        toast.error("Please add your address before proceeding");
        return;
      }

      // Calculate amount in paise (Razorpay expects amount in paise)
      const amount = Math.round(cart.reduce((sum, item) => sum + Number(item.price), 0) * 100);

      if (amount < 100) {
        toast.error("Order amount must be at least ‚Çπ1");
        return;
      }

      // Show loading state
      const loadingToast = toast.loading("Creating order...");

      try {
        // Create order
        const { data } = await axios.post(
          "/api/v1/payment/create-order",
          { amount },
          {
            headers: {
              Authorization: auth.token
            }
          }
        );

        // Clear loading state
        toast.dismiss(loadingToast);

        if (!data?.success || !data?.order?.id) {
          toast.error(data?.message || "Could not create order");
          return;
        }

        // Configure Razorpay options
        const options = {
          key: process.env.REACT_APP_RAZORPAY_KEY_ID || "rzp_test_OxAxRfdIt6ofSm", // Prefer environment variable
          amount: data.order.amount,
          currency: data.order.currency,
          name: "Car Rental Service",
          description: "Vehicle Rental Payment",
          order_id: data.order.id,
          prefill: {
            name: auth?.user?.name || "",
            email: auth?.user?.email || "",
            contact: auth?.user?.phone || ""
          },
          notes: {
            address: auth?.user?.address || ""
          },
          theme: {
            color: "#3399cc"
          },
          handler: async function(response) {
            try {
              const verifyToast = toast.loading("Verifying payment...");

              // Prepare minimal cart data
              const minimalCart = cart.map(item => ({
                _id: item._id,
                name: item.name,
                price: item.price
              }));

              const verifyData = {
                razorpay_order_id: response.razorpay_order_id,
                razorpay_payment_id: response.razorpay_payment_id,
                razorpay_signature: response.razorpay_signature,
                cart: minimalCart,
                buyer: auth?.user?._id
              };

              const { data: verifyResponse } = await axios.post(
                "/api/v1/payment/verify-payment",
                verifyData,
                {
                  headers: {
                    Authorization: auth.token,
                    'Content-Type': 'application/json'
                  }
                }
              );

              toast.dismiss(verifyToast);

              if (verifyResponse.success) {
                // Clear cart and local storage
                setCart([]);
                localStorage.removeItem("cart");

                // Show success message
                toast.success(verifyResponse.message || "Payment successful!");

                // Small delay before navigation to ensure toast is visible
                setTimeout(() => {
                  navigate("/dashboard/user/orders");
                }, 1500);
              } else {
                toast.error(verifyResponse.message || "Payment verification failed");
                console.error("Verification failed:", verifyResponse);
              }
            } catch (error) {

              console.error("Verification error:", error);
              toast.error(
                error.response?.data?.message || 
                "Payment verification failed. Please contact support if amount was deducted."
              );
            }
          },
          modal: {
            ondismiss: function() {
              toast.error("Payment cancelled by user");
            },
            escape: false,
            backdropclose: false
          }
        };

        const razorpayInstance = new window.Razorpay(options);

        razorpayInstance.on("payment.failed", function(response) {
          toast.dismiss();
          console.error("Payment failed:", response.error);
          toast.error(response.error.description || "Payment failed");
        });

        razorpayInstance.open();
      } catch (error) {
        toast.dismiss(loadingToast);
        console.error("Order creation error:", error);
        toast.error(
          error.response?.data?.message || 
          "Error creating order. Please try again."
        );
      }
    } catch (error) {
      toast.dismiss();
      console.error("Payment error:", error);
      toast.error(
        error.response?.data?.message || 
        "Error initiating payment. Please try again."
      );
    }
  };

  return (
    <Layout>
      <div className="container cart-page">
        <h2 className="text-center cart-title">
          <span role="img" aria-label="cart">üõí</span> Your Shopping Cart
        </h2>
        <p className="text-center cart-subtitle">
          {cart?.length
            ? `You have ${cart.length} item(s) in your cart.`
            : "Your cart is empty. Start shopping now!"}
        </p>

        <div className="row">
          <div className="col-lg-7">
            <div className="row row-cols-1 row-cols-md-2 g-4">
              {cart?.map((item) => (
                <div className="col" key={item._id}>
                  <div className="card cart-item-card">
                    <img
                      src={`/api/v1/product/product-photo/${item._id}`}
                      alt={item.name}
                      className="card-img-top cart-item-image"
                    />
                    <div className="card-body">
                      <h5 className="card-title">{item.name}</h5>
                      <p className="card-text">
