import React, { useState, useEffect, useCallback } from "react";
import { useNavigate } from "react-router-dom";
import { Checkbox, Radio } from "antd";
import { Prices } from "../components/Prices";
import { useCart } from "../context/cart";
import axios from "axios";
import toast from "react-hot-toast";
import Layout from "./../components/Layout/Layout";
import { AiOutlineReload } from "react-icons/ai";
import { FaSearch, FaCar, FaMoneyBillWave, FaClock, FaShieldAlt, FaMapMarkerAlt } from "react-icons/fa";
import "../styles/Homepage.css";
import Slider from "react-slick";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";

const HomePage = () => {
  const navigate = useNavigate();
  const [cart, setCart] = useCart();
  const [products, setProducts] = useState([]);
  const [categories, setCategories] = useState([]);
  const [checked, setChecked] = useState([]);
  const [radio, setRadio] = useState([]);
  const [total, setTotal] = useState(0);
  const [page, setPage] = useState(1);
  const [loading, setLoading] = useState(false);

  // Banner slider settings
  const bannerSettings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 1,
    slidesToScroll: 1,
    autoplay: true,
    autoplaySpeed: 5000,
    fade: true,
    cssEase: "linear",
    pauseOnHover: false
  };

  // Banner data with unique IDs
  const bannerSlides = [
    {
      id: 1,
      image: "https://images.unsplash.com/photo-1503376780353-7e6692767b70?auto=format&fit=crop&q=80",
      title: "Luxury Car Rentals",
      subtitle: "Experience the Ultimate Driving Pleasure",
      cta: "Explore Fleet"
    },
    {
      id: 2,
      image: "https://images.unsplash.com/photo-1492144534655-ae79c964c9d7?auto=format&fit=crop&q=80",
      title: "Premium Sports Cars",
      subtitle: "Feel the Power of Performance",
      cta: "View Collection"
    },
    {
      id: 3,
      image: "https://images.unsplash.com/photo-1583121274602-3e2820c69888?auto=format&fit=crop&q=80",
      title: "Executive Class",
      subtitle: "Travel in Style and Comfort",
      cta: "Book Now"
    }
  ];

  // Features data with unique IDs
  const features = [
    {
      id: 'feature-1',
      icon: <FaCar className="feature-icon" />,
      title: "Premium Fleet",
      description: "Luxury and sports cars maintained to perfection"
    },
    {
      id: 'feature-2',
      icon: <FaMoneyBillWave className="feature-icon" />,
      title: "Best Rates",
      description: "Competitive pricing with no hidden charges"
    },
    {
      id: 'feature-3',
      icon: <FaClock className="feature-icon" />,
      title: "24/7 Support",
      description: "Round-the-clock customer assistance"
    },
    {
      id: 'feature-4',
      icon: <FaShieldAlt className="feature-icon" />,
      title: "Fully Insured",
      description: "Comprehensive insurance coverage included"
    },
    {
      id: 'feature-5',
      icon: <FaMapMarkerAlt className="feature-icon" />,
      title: "Flexible Pickup",
      description: "Convenient pickup and drop-off locations"
    },
    {
      id: 'feature-6',
      icon: <FaSearch className="feature-icon" />,
      title: "Easy Booking",
      description: "Simple and secure reservation process"
    }
  ];

  // Fetch all categories
  const getAllCategory = async () => {
    try {
      const { data } = await axios.get("/api/v1/category/get-category");
      if (data?.success) {
        setCategories(data.category);
      }
    } catch (error) {
      console.error("Error fetching categories:", error);
    }
  };

  // Fetch total product count
  const getTotal = async () => {
    try {
      const { data } = await axios.get("/api/v1/product/product-count");
      setTotal(data?.total);
    } catch (error) {
      console.error("Error fetching total product count:", error);
    }
  };

  // Fetch products based on page
  const getAllProducts = useCallback(async () => {
    try {
      setLoading(true);
      const { data } = await axios.get(`/api/v1/product/product-list/${page}`);
      setLoading(false);
      setProducts(data.products);
    } catch (error) {
      setLoading(false);
      console.error("Error fetching products:", error);
    }
  }, [page]);

  // Fetch filtered products
  const filterProduct = useCallback(async () => {
    try {
      const { data } = await axios.post("/api/v1/product/product-filters", {
        checked,
        radio,
      });
      setProducts(data?.products || []);
    } catch (error) {
      console.error("Error filtering products:", error);
    }
  }, [checked, radio]);

  // Handle category filter
  const handleFilter = (value, id) => {
    const updatedChecked = value
      ? [...checked, id]
      : checked.filter((c) => c !== id);
    setChecked(updatedChecked);
  };

  useEffect(() => {
    getAllProducts();
  }, [getAllProducts]);

  useEffect(() => {
    if (!checked.length || !radio.length) getAllProducts();
  }, [checked, radio, getAllProducts]);

  useEffect(() => {
    if (checked.length || radio.length) filterProduct();
  }, [checked, radio, filterProduct]);

  useEffect(() => {
    getAllCategory();
    getTotal();
  }, []);

  return (
    <Layout title={"Premium Car Rentals - Best Offers"}>
      <div className="homepage">
        {/* Hero Section */}
        <div className="banner-section">
          <Slider {...bannerSettings}>
            {bannerSlides.map((slide) => (
              <div key={slide.id} className="banner-slide">
                <div 
                  className="banner-image" 
                  style={{ backgroundImage: `url(${slide.image})` }}
                >
                  <div className="banner-content">
                    <h1 className="banner-title">{slide.title}</h1>
                    <p className="banner-subtitle">{slide.subtitle}</p>
                    <button 
                      className="banner-cta"
                      onClick={() => navigate("/categories")}
                    >
                      {slide.cta}
                    </button>
                  </div>
                </div>
              </div>
            ))}
          </Slider>
        </div>

        {/* Features Section */}
        <div className="features-section">
          <div className="container">
            <h2 className="section-title">Why Choose Us</h2>
            <div className="row g-4">
              {features.map((feature) => (
                <div className="col-lg-4 col-md-6" key={feature.id}>
                  <div className="feature-card">
                    {feature.icon}
                    <h3>{feature.title}</h3>
                    <p>{feature.description}</p>
                  </div>
                </div>
              ))}
            </div>
          </div>
        </div>

        {/* Products Section */}
        <div className="products-section">
          <div className="container-fluid">
            <div className="row">
              {/* Filters */}
              <div className="col-lg-3 col-md-4">
                <div className="filters-wrapper">
                  <div className="filters">
                    <h4 className="filter-title">Filter By Category</h4>
                    <div className="filter-options">
                      {categories?.map((c) => (
                        <Checkbox
                          key={`category-${c._id}`}
                          onChange={(e) => handleFilter(e.target.checked, c._id)}
                        >
                          {c.name}
                        </Checkbox>
                      ))}
                    </div>

                    <h4 className="filter-title">Filter By Price</h4>
                    <div className="filter-options">
                      <Radio.Group onChange={(e) => setRadio(e.target.value)}>
                        {Prices?.map((p) => (
                          <div key={`price-${p._id}`} className="price-option">
                            <Radio value={p.array}>{p.name}</Radio>
                          </div>
                        ))}
                      </Radio.Group>
                    </div>

                    <button
                      className="reset-filters-btn"
                      onClick={() => {
                        setChecked([]);
                        setRadio([]);
                        window.location.reload();
                      }}
                    >
                      Reset Filters
                    </button>
                  </div>
                </div>
              </div>

              {/* Products Grid */}
              <div className="col-lg-9 col-md-8">
                <div className="products-wrapper">
                  <h2 className="section-title">Available Cars</h2>
                  <div className="row">
                    {products?.map((p) => (
                      <div className="col-xl-4 col-lg-6 col-md-6" key={`product-${p._id}`}>
                        <div className="product-card">
                          <div className="product-image">
                            <img
                              src={`/api/v1/product/product-photo/${p._id}`}
                              alt={p.name}
                            />
                          </div>
                          <div className="product-content">
                            <h3 className="product-title">{p.name}</h3>
                            <div className="product-price">
                              {p.price.toLocaleString("en-IN", {
                                style: "currency",
                                currency: "INR",
                              })}
                              <span className="price-period">/day</span>
                            </div>
                            <p className="product-description">
                              {p.description.substring(0, 60)}...
                            </p>
                            <div className="product-actions">
                              <button
                                className="btn-view"
                                onClick={() => navigate(`/product/${p.slug}`)}
                              >
                                View Details
                              </button>
                              <button
                                className="btn-rent"
                                onClick={() => {
                                  setCart([...cart, p]);
                                  localStorage.setItem(
                                    "cart",
                                    JSON.stringify([...cart, p])
                                  );
                                  toast.success("Added to rental cart");
                                }}
                              >
                                Rent Now
                              </button>
                            </div>
                          </div>
                        </div>
                      </div>
                    ))}
                  </div>

                  {/* Load More */}
                  {products && products.length < total && (
                    <div className="load-more">
                      <button
                        className="load-more-btn"
                        onClick={() => setPage(page + 1)}
                        disabled={loading}
                      >
                        {loading ? (
                          "Loading..."
                        ) : (
                          <>
                            Load More <AiOutlineReload />
                          </>
                        )}
                      </button>
                    </div>
                  )}
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </Layout>
  );
};

export default HomePage;
