import React, { useState, useEffect, useCallback, useMemo } from "react";
import axios from "axios";
import toast from "react-hot-toast";
import AdminMenu from "../../components/Layout/AdminMenu";
import Layout from "../../components/Layout/Layout";
import { useAuth } from "../../context/auth";
import moment from "moment";
import { Select } from "antd";
const { Option } = Select;

const AdminOrders = () => {
  const [status] = useState([
    "Processing",
    "Shipped",
    "Delivered",
    "Cancelled"
  ]);
  const [orders, setOrders] = useState([]);
  const [loading, setLoading] = useState(false);
  const [auth] = useAuth();

  // Memoize the auth token
  const authToken = useMemo(() => auth?.token, [auth]);

  // Format price for display
  const formatPrice = (price) => {
    return price.toLocaleString("en-IN", {
      style: "currency",
      currency: "INR",
    });
  };

  const getOrders = useCallback(async () => {
    if (!authToken) return;

    try {
      setLoading(true);
      const { data } = await axios.get("/api/v1/payment/all-orders", {
        headers: {
          Authorization: authToken,
        },
      });
      if (data?.success) {
        const ordersWithProducts = data.orders.map(order => ({
          ...order,
          products: order.products.map(product => ({
            ...product,
            productId: product.product?._id || product.product
          }))
        }));
        setOrders(ordersWithProducts);
      }
      setLoading(false);
    } catch (error) {
      console.error("Error fetching orders:", error);
      toast.error("Error fetching orders");
      setLoading(false);
    }
  }, [authToken]);

  useEffect(() => {
    if (authToken) {
      getOrders();
    }
  }, [authToken, getOrders]);

  const handleStatusChange = async (orderId, value) => {
    try {
      setLoading(true);
      const { data } = await axios.put(
        `/api/v1/payment/order-status/${orderId}`,
        { status: value },
        {
          headers: {
            Authorization: authToken,
          },
        }
      );
      if (data?.success) {
        getOrders();
        toast.success("Order status updated");
      }
    } catch (error) {
      console.error("Error updating order status:", error);
      toast.error("Error updating order status");
    } finally {
      setLoading(false);
    }
  };

  return (
    <Layout title={"All Orders - Admin"}>
      <div className="container-fluid m-3 p-3">
        <div className="row">
          <div className="col-md-3">
            <AdminMenu />
          </div>
          <div className="col-md-9">
            <h1 className="text-center mb-4">Manage Orders</h1>

            {loading ? (
              <div className="text-center">
                <div className="spinner-border" role="status">
                  <span className="visually-hidden">Loading...</span>
                </div>
              </div>
            ) : orders?.length === 0 ? (
              <div className="text-center">
                <h3>No orders found</h3>
              </div>
            ) : (
              orders?.map((order) => (
                <div className="card shadow-sm mb-4" key={order._id}>
                  <div className="card-header bg-light">
                    <div className="row align-items-center">
                      <div className="col-md-4">
                        <h5 className="mb-0">Order ID: {order._id}</h5>
                      </div>
                      <div className="col-md-4">
                        <Select
                          className="w-100"
                          value={order.status}
                          onChange={(value) => handleStatusChange(order._id, value)}
                          disabled={loading}
                        >
                          {status.map((s) => (
                            <Option key={s} value={s}>
                              {s}
                            </Option>
                          ))}
                        </Select>
                      </div>
                      <div className="col-md-4 text-end">
                        <p className="mb-0">
                          <strong>Amount:</strong> {formatPrice(order.amount)}
                        </p>
                      </div>
                    </div>
                  </div>

                  <div className="card-body">
                    <div className="row mb-3">
                      <div className="col-md-6">
                        <p><strong>Customer:</strong> {order.buyer?.name}</p>
                        <p><strong>Contact:</strong> {order.buyer?.phone}</p>
                        <p><strong>Email:</strong> {order.buyer?.email}</p>
                      </div>
                      <div className="col-md-6">
                        <p><strong>Order Date:</strong> {moment(order.createdAt).format("MMMM Do YYYY, h:mm a")}</p>
                        <p><strong>Payment ID:</strong> {order.payment?.razorpay_payment_id}</p>
                        <p><strong>Shipping Address:</strong> {order.buyer?.address}</p>
                      </div>
                    </div>

                    <h6 className="mb-3">Ordered Items</h6>
                    <div className="row">
                      {order.products?.map((item) => (
                        <div className="col-md-6 mb-3" key={item.productId}>
                          <div className="card h-100">
                            <div className="row g-0">
                              <div className="col-md-4">
                                <img
                                  src={`/api/v1/product/product-photo/${item.productId}`}
                                  alt={item.name}
                                  className="img-fluid rounded-start"
                                  style={{ height: "150px", objectFit: "cover" }}
                                  onError={(e) => {
                                    e.target.onerror = null;
                                    e.target.src = "/placeholder-car.jpg"; // Add a placeholder image
                                  }}
                                />
                              </div>
                              <div className="col-md-8">
                                <div className="card-body">
                                  <h6 className="card-title">{item.name}</h6>
                                  <p className="card-text">
                                    <small className="text-muted">
                                      Price: {formatPrice(item.price)}
                                    </small>
                                  </p>
                                </div>
                              </div>
                            </div>
                          </div>
                        </div>
                      ))}
                    </div>
                  </div>
                </div>
              ))
            )}
          </div>
        </div>
      </div>
    </Layout>
  );
};

export default AdminOrders;
