import React, { useState, useEffect } from "react";
import Layout from "../components/Layout/Layout";
import { useCart } from "../context/cart";
import { useAuth } from "../context/auth";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import "../styles/CartStyles.css";
import { toast } from "react-hot-toast";

const CartPage = () => {
  const [auth] = useAuth();
  const [cart, setCart] = useCart();
  const navigate = useNavigate();
  const [loading, setLoading] = useState(false);

  // Load Razorpay script 
  useEffect(() => {
    const loadRazorpayScript = async () => {
      return new Promise((resolve) => {
        const script = document.createElement("script");
        script.src = "https://checkout.razorpay.com/v1/checkout.js";
        script.async = true;
        script.onload = () => resolve(true);
        script.onerror = () => {
          script.remove();
          resolve(false);
        };
        document.body.appendChild(script);
      });
    };
    loadRazorpayScript();
  }, []);

  // Calculate total price in rupees
  const calculateTotal = () => {
    try {
      return cart.reduce((sum, item) => sum + Number(item.price), 0);
    } catch (error) {
      console.error("Error calculating total:", error);
      return 0;
    }
  };

  // Format price for display
  const formatPrice = (price) => {
    return price.toLocaleString("en-IN", {
      style: "currency",
      currency: "INR",
    });
  };

  // Remove item from cart
  const removeCartItem = (pid) => {
    try {
      const updatedCart = cart.filter((item) => item._id !== pid);
      setCart(updatedCart);
      localStorage.setItem("cart", JSON.stringify(updatedCart));
      toast.success("Item removed from cart");
    } catch (error) {
      console.error("Error removing item:", error);
      toast.error("Could not remove item");
    }
  };

  // Handle payment process
  const handlePayment = async () => {
    try {
      // Basic validations
      if (!window.Razorpay) {
        toast.error("Payment gateway is loading. Please try again.");
        return;
      }

      if (!auth?.token) {
        toast.error("Please login to proceed");
        navigate("/login");
        return;
      }

      if (!cart.length) {
        toast.error("Your cart is empty");
        return;
      }

      if (!auth?.user?.address) {
        toast.error("Please add your address before proceeding");
        return;
      }

      setLoading(true);

      // Calculate amount in paise
      const totalAmount = Math.round(calculateTotal() * 100);

      if (totalAmount < 100) {
        toast.error("Order amount must be at least ₹1");
        setLoading(false);
        return;
      }

      // Create order
      const { data } = await axios.post(
        "/api/v1/payment/create-order",
        { amount: totalAmount },
        {
          headers: {
            Authorization: auth.token
          }
        }
      );

      if (!data?.success) {
        throw new Error(data?.message || "Could not create order");
      }

      // Configure Razorpay options
      const options = {
        key: process.env.REACT_APP_RAZORPAY_KEY_ID,
        amount: data.order.amount,
        currency: data.order.currency,
        name: "Car Rental Service",
        description: "Vehicle Rental Payment",
        order_id: data.order.id,
        prefill: {
          name: auth?.user?.name || "",
          email: auth?.user?.email || "",
          contact: auth?.user?.phone || ""
        },
        notes: {
          address: auth?.user?.address || ""
        },
        theme: {
          color: "#3399cc"
        },
        modal: {
          ondismiss: function() {
            setLoading(false);
            toast.error("Payment cancelled");
          },
          escape: false,
          backdropclose: false
        },
        handler: async function(response) {
          try {
            setLoading(true);

            // Prepare order data
            const orderData = {
              razorpay_order_id: response.razorpay_order_id,
              razorpay_payment_id: response.razorpay_payment_id,
              razorpay_signature: response.razorpay_signature,
              products: cart.map(item => ({
                product: item._id,
                name: item.name,
                price: Number(item.price)
              })),
              buyer: auth?.user?._id,
              amount: calculateTotal()
            };

            // Verify payment
            const { data: verifyResponse } = await axios.post(
              "/api/v1/payment/verify-payment",
              orderData,
              {
                headers: {
                  Authorization: auth.token,
                  'Content-Type': 'application/json'
                }
              }
            );

            if (verifyResponse.success) {
              // Clear cart
              setCart([]);
              localStorage.removeItem("cart");

              toast.success("Payment successful! Redirecting to orders...");

              // Navigate to orders page after a short delay
              setTimeout(() => {
                navigate("/dashboard/user/orders");
              }, 2000);
            } else {
              throw new Error(verifyResponse.message || "Payment verification failed");
            }
          } catch (error) {
            console.error("Payment verification error:", error);
            toast.error(error.response?.data?.message || "Payment verification failed");
          } finally {
            setLoading(false);
          }
        }
      };

      const razorpayInstance = new window.Razorpay(options);

      razorpayInstance.on("payment.failed", function(response) {
        console.error("Payment failed:", response.error);
        toast.error(response.error.description || "Payment failed");
        setLoading(false);
      });

      razorpayInstance.open();
    } catch (error) {
      console.error("Payment error:", error);
      toast.error(error.response?.data?.message || "Error processing payment");
      setLoading(false);
    }
  };

  return (
    <Layout>
      <div className="container cart-page">
        <div className="text-center mb-4">
          <h2 className="cart-title">
            <span role="img" aria-label="cart">🛒</span> Your Shopping Cart
          </h2>
          <p className="cart-subtitle">
            {cart?.length
              ? `You have ${cart.length} item(s) in your cart`
              : "Your cart is empty"}
          </p>
        </div>

        <div className="row">
          <div className="col-lg-7">
            <div className="row row-cols-1 row-cols-md-2 g-4">
              {cart?.map((item) => (
                <div className="col" key={item._id}>
                  <div className="card h-100 cart-item-card">
                    <img
                      src={`/api/v1/product/product-photo/${item._id}`}
                      alt={item.name}
                      className="card-img-top cart-item-image"
                      loading="lazy"
                    />
                    <div className="card-body d-flex flex-column">
                      <h5 className="card-title">{item.name}</h5>
                      <p className="card-text flex-grow-1">
                        {item.description?.substring(0, 60)}...
                      </p>
                      <div className="mt-auto">
                        <h6 className="cart-price mb-3">
                          Price: {formatPrice(item.price)}/day
                        </h6>
                        <button
                          className="btn btn-danger w-100"
                          onClick={() => removeCartItem(item._id)}
                          disabled={loading}
                        >
                          <span role="img" aria-label="remove">❌</span> Remove
                        </button>
                      </div>
                    </div>
                  </div>
                </div>
              ))}
            </div>
          </div>

          <div className="col-lg-5 mt-4 mt-lg-0">
            <div className="card shadow-lg p-4 cart-summary">
              <h3 className="text-center mb-4">
                <span role="img" aria-label="order">🛍️</span> Order Summary
              </h3>
              <hr />
              <h4 className="text-center total-price mb-4">
                Total: {formatPrice(calculateTotal())}
              </h4>

              {auth?.user?.address ? (
                <div className="address-section mb-4">
                  <h5>
                    <span role="img" aria-label="location">📍</span> Delivery Address:
                  </h5>
                  <p className="mb-3">{auth.user.address}</p>
                  <button
                    className="btn btn-outline-primary w-100"
                    onClick={() => navigate("/dashboard/user/profile")}
                    disabled={loading}
                  >
                    <span role="img" aria-label="edit">✏️</span> Update Address
                  </button>
                </div>
              ) : auth?.token ? (
                <button
                  className="btn btn-outline-primary w-100 mb-4"
                  onClick={() => navigate("/dashboard/user/profile")}
                  disabled={loading}
                >
                  <span role="img" aria-label="add">➕</span> Add Address
                </button>
              ) : (
                <button
                  className="btn btn-warning w-100 mb-4"
                  onClick={() => navigate("/login", { state: { from: "/cart" } })}
                  disabled={loading}
                >
                  <span role="img" aria-label="login">🔑</span> Login to Proceed
                </button>
              )}

              {auth?.token && cart.length > 0 && auth.user.address && (
                <button
                  className="btn btn-success w-100"
                  onClick={handlePayment}
                  disabled={loading}
                >
                  {loading ? (
                    <>
                      <span className="spinner-border spinner-border-sm me-2" role="status" aria-hidden="true"></span>
                      Processing...
                    </>
                  ) : (
                    <>
                      <span role="img" aria-label="pay">💳</span> Proceed to Pay
                    </>
                  )}
                </button>
              )}
            </div>
          </div>
        </div>
      </div>
    </Layout>
  );
};

export default CartPage;
