import React, { useState, useEffect } from "react";
import { NavLink, Link } from "react-router-dom";
import { useAuth } from "../../context/auth";
import toast from "react-hot-toast";
import SearchInput from "../Form/SearchInput";
import useCategory from "../../hooks/useCategory";
import { useCart } from "../../context/cart";
import { Badge } from "antd";
import { FaShoppingBag, FaUserCircle, FaCarAlt, FaBars, FaSignOutAlt } from "react-icons/fa";
import "./Header.css";

const Header = () => {
  const [auth, setAuth] = useAuth();
  const [cart] = useCart();
  const categories = useCategory();
  const [isScrolled, setIsScrolled] = useState(false);
  const [isMenuOpen, setIsMenuOpen] = useState(false);

  // Handle scroll effect
  useEffect(() => {
    const handleScroll = () => {
      setIsScrolled(window.scrollY > 50);
    };
    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  const handleLogout = () => {
    setAuth({
      ...auth,
      user: null,
      token: "",
    });
    localStorage.removeItem("auth");
    toast.success("Logout Successfully");
  };

  const toggleMenu = () => {
    setIsMenuOpen(!isMenuOpen);
  };

  return (
    <nav className={`main-header ${isScrolled ? 'scrolled' : ''}`}>
      <div className="container">
        {/* Logo Section */}
        <Link to="/" className="logo">
          <FaCarAlt className="logo-icon" />
          <span className="logo-text">
            <span className="logo-easy">Easy</span>
            <span className="logo-carz">Carz</span>
          </span>
        </Link>

        {/* Mobile Menu Button */}
        <button
          className="mobile-menu-btn"
          type="button"
          onClick={toggleMenu}
        >
          <FaBars />
        </button>

        {/* Navigation Menu */}
        <div className={`navbar-collapse ${isMenuOpen ? 'show' : ''}`}>
          <ul className="nav-links">
            <li>
              <NavLink to="/" className={({ isActive }) => isActive ? 'active' : ''}>
                Home
              </NavLink>
            </li>

            {/* Categories Dropdown */}
            <li className="dropdown">
              <Link
                className="dropdown-toggle"
                to="/categories"
              >
                Categories
              </Link>
              <ul className="dropdown-menu">
                {categories?.map((c) => (
                  <li key={c._id}>
                    <Link className="dropdown-item" to={`/category/${c.slug}`}>
                      {c.name}
                    </Link>
                  </li>
                ))}
              </ul>
            </li>
          </ul>

          <div className="search-wrapper">
            <SearchInput />
          </div>

          <ul className="nav-links">
            {/* User Authentication */}
            {!auth?.user ? (
              <li className="auth-buttons">
                <NavLink to="/login" className="btn-login">
                  Login
                </NavLink>
                <NavLink to="/register" className="btn-register">
                  Register
                </NavLink>
              </li>
            ) : (
              <li className="user-dropdown">
                <NavLink
                  to={`/dashboard/${auth?.user?.role === 1 ? "admin" : "user"}`}
                  className="dropdown-toggle"
                >
                  <FaUserCircle className="user-icon" />
                  <span>{auth?.user?.name}</span>
                </NavLink>
                <ul className="dropdown-menu">
                  <li>
                    <NavLink
                      to={`/dashboard/${auth?.user?.role === 1 ? "admin" : "user"}`}
                      className="dropdown-item"
                    >
                      Dashboard
                    </NavLink>
                  </li>
                  <li>
                    <NavLink
                      to="/profile"
                      className="dropdown-item"
                    >
                      Profile
                    </NavLink>
                  </li>
                </ul>
              </li>
            )}

            {/* Cart */}
            <li className="cart-item">
              <NavLink to="/cart">
                <Badge count={cart?.length} showZero offset={[10, -5]}>
                  <FaShoppingBag className="cart-icon" />
                </Badge>
              </NavLink>
            </li>

            {/* Logout Dropdown */}
            {auth?.user && (
              <li className="logout-dropdown">
                <button onClick={handleLogout} className="logout-dropdown-btn">
                  <FaSignOutAlt className="logout-icon" />
                  <span>Logout</span>
                </button>
              </li>
            )}
          </ul>
        </div>
      </div>
    </nav>
  );
};

export default Header;
