import React, { useState, useEffect } from "react";
import Layout from "./../../components/Layout/Layout";
import AdminMenu from "./../../components/Layout/AdminMenu";
import toast from "react-hot-toast";
import axios from "axios";
import { Select } from "antd";
import { useNavigate } from "react-router-dom";
import { FaTrash, FaPlus } from "react-icons/fa";
import "./CreateProduct.css";

const { Option } = Select;

const CreateProduct = () => {
  const navigate = useNavigate();
  const [categories, setCategories] = useState([]);
  const [formData, setFormData] = useState({
    name: "",
    description: "",
    price: "",
    category: "",
    quantity: "",
    features: [],
    specifications: []
  });
  const [mainPhoto, setMainPhoto] = useState(null);
  const [additionalPhotos, setAdditionalPhotos] = useState([]);
  const [loading, setLoading] = useState(false);

  // Get all categories
  const getAllCategory = async () => {
    try {
      const { data } = await axios.get("/api/v1/category/get-category");
      if (data?.success) {
        setCategories(data?.category);
      }
    } catch (error) {
      console.log(error);
      toast.error("Error fetching categories");
    }
  };

  useEffect(() => {
    getAllCategory();
  }, []);

  // Handle form input changes
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  // Handle main photo change
  const handleMainPhotoChange = (e) => {
    const file = e.target.files[0];
    if (file && file.type.startsWith('image/')) {
      setMainPhoto(file);
    } else {
      toast.error("Please select a valid image file");
    }
  };

  // Handle additional photos change
  const handleAdditionalPhotosChange = (e) => {
    const files = Array.from(e.target.files);
    const validFiles = files.filter(file => file.type.startsWith('image/'));
    if (validFiles.length + additionalPhotos.length > 5) {
      toast.error("Maximum 5 additional photos allowed");
      return;
    }
    setAdditionalPhotos(prev => [...prev, ...validFiles]);
  };

  // Remove additional photo
  const removeAdditionalPhoto = (index) => {
    setAdditionalPhotos(prev => prev.filter((_, i) => i !== index));
  };

  // Handle features
  const addFeature = () => {
    setFormData(prev => ({
      ...prev,
      features: [...prev.features, ""]
    }));
  };

  const removeFeature = (index) => {
    setFormData(prev => ({
      ...prev,
      features: prev.features.filter((_, i) => i !== index)
    }));
  };

  const updateFeature = (index, value) => {
    setFormData(prev => ({
      ...prev,
      features: prev.features.map((feature, i) => i === index ? value : feature)
    }));
  };

  // Handle specifications
  const addSpecification = () => {
    setFormData(prev => ({
      ...prev,
      specifications: [...prev.specifications, { key: "", value: "" }]
    }));
  };

  const removeSpecification = (index) => {
    setFormData(prev => ({
      ...prev,
      specifications: prev.specifications.filter((_, i) => i !== index)
    }));
  };

  const updateSpecification = (index, field, value) => {
    setFormData(prev => ({
      ...prev,
      specifications: prev.specifications.map((spec, i) => 
        i === index ? { ...spec, [field]: value } : spec
      )
    }));
  };

  // Create product function
  const handleCreate = async (e) => {
    e.preventDefault();
    try {
      setLoading(true);

      // Validation
      if (!mainPhoto) {
        toast.error("Main photo is required");
        setLoading(false);
        return;
      }
      if (!formData.name || !formData.description || !formData.price || !formData.category) {
        toast.error("Please fill all required fields");
        setLoading(false);
        return;
      }

      // Create FormData object
      const productData = new FormData();

      // Append basic fields
      productData.append("name", formData.name);
      productData.append("description", formData.description);
      productData.append("price", formData.price);
      productData.append("category", formData.category);
      productData.append("quantity", formData.quantity || 1);

      // Append features if any
      if (formData.features && formData.features.length > 0) {
        productData.append("features", JSON.stringify(formData.features.filter(f => f.trim() !== "")));
      }

      // Append specifications if any
      if (formData.specifications && formData.specifications.length > 0) {
        const validSpecs = formData.specifications.filter(spec => spec.key.trim() !== "" && spec.value.trim() !== "");
        productData.append("specifications", JSON.stringify(validSpecs));
      }

      // Append main photo
      productData.append("mainPhoto", mainPhoto);

      // Append additional photos
      if (additionalPhotos.length > 0) {
        additionalPhotos.forEach(photo => {
          productData.append("additionalPhotos", photo);
        });
      }

      // Make API call
      const { data } = await axios.post(
        "/api/v1/product/create-product",
        productData,
        {
          headers: {
            "Content-Type": "multipart/form-data",
          },
        }
      );

      if (data?.success) {
        toast.success("Product Created Successfully");
        navigate("/dashboard/admin/products");
      } else {
        toast.error(data?.message || "Error creating product");
      }
    } catch (error) {
      console.log(error);
      toast.error(error.response?.data?.error || "Something went wrong");
    } finally {
      setLoading(false);
    }
  };

  return (
    <Layout title={"Dashboard - Create Product"}>
      <div className="container-fluid m-3 p-3">
        <div className="row">
          <div className="col-md-3">
            <AdminMenu />
          </div>
          <div className="col-md-9">
            <div className="create-product-form">
              <h1 className="form-title">Create New Car Listing</h1>

              {/* Category Selection */}
              <div className="form-section">
                <h3 className="section-title">Category</h3>
                <Select
                  bordered={false}
                  placeholder="Select a category"
                  size="large"
                  showSearch
                  className="form-select mb-3"
                  onChange={(value) => setFormData(prev => ({ ...prev, category: value }))}
                >
                  {categories?.map((c) => (
                    <Option key={c._id} value={c._id}>
                      {c.name}
                    </Option>
                  ))}
                </Select>
              </div>

              {/* Photo Upload Section */}
              <div className="form-section">
                <h3 className="section-title">Photos</h3>

                {/* Main Photo */}
                <div className="photo-upload-container">
                  <div className="main-photo-upload">
                    <label className="upload-label">
                      Main Photo *
                      <input
                        type="file"
                        accept="image/*"
                        onChange={handleMainPhotoChange}
                        className="hidden-input"
                      />
                    </label>
                    {mainPhoto && (
                      <div className="photo-preview">
                        <img
                          src={URL.createObjectURL(mainPhoto)}
                          alt="Main preview"
                        />
                        <button 
                          type="button" 
                          className="remove-photo"
                          onClick={() => setMainPhoto(null)}
                        >
                          <FaTrash />
                        </button>
                      </div>
                    )}
                  </div>

                  {/* Additional Photos */}
                  <div className="additional-photos">
                    <label className="upload-label">
                      Additional Photos (Max 5)
                      <input
                        type="file"
                        accept="image/*"
                        multiple
                        onChange={handleAdditionalPhotosChange}
                        className="hidden-input"
                      />
                    </label>
                    <div className="photos-grid">
                      {additionalPhotos.map((photo, index) => (
                        <div key={index} className="photo-preview">
                          <img
                            src={URL.createObjectURL(photo)}
                            alt={`Preview ${index + 1}`}
                          />
                          <button 
                            type="button"
                            className="remove-photo"
                            onClick={() => removeAdditionalPhoto(index)}
                          >
                            <FaTrash />
                          </button>
                        </div>
                      ))}
                    </div>
                  </div>
                </div>
              </div>

              {/* Basic Information */}
              <div className="form-section">
                <h3 className="section-title">Basic Information</h3>
                <input
                  type="text"
                  name="name"
                  value={formData.name}
                  placeholder="Car Name *"
                  className="form-control mb-3"
                  onChange={handleInputChange}
                />
                <textarea
                  name="description"
                  value={formData.description}
                  placeholder="Description *"
                  className="form-control mb-3"
                  rows="4"
                  onChange={handleInputChange}
                />
                <div className="row">
                  <div className="col-md-6">
                    <input
                      type="number"
                      name="price"
                      value={formData.price}
                      placeholder="Daily Rental Price (₹) *"
                      className="form-control mb-3"
                      onChange={handleInputChange}
                    />
                  </div>
                  <div className="col-md-6">
                    <input
                      type="number"
                      name="quantity"
                      value={formData.quantity}
                      placeholder="Available Units"
                      className="form-control mb-3"
                      onChange={handleInputChange}
                    />
                  </div>
                </div>
              </div>

              {/* Features Section */}
              <div className="form-section">
                <h3 className="section-title">
                  Features
                  <button type="button" className="btn-add" onClick={addFeature}>
                    <FaPlus /> Add Feature
                  </button>
                </h3>
                {formData.features.map((feature, index) => (
                  <div key={index} className="feature-input">
                    <input
                      type="text"
                      value={feature}
                      placeholder="Enter feature"
                      className="form-control"
                      onChange={(e) => updateFeature(index, e.target.value)}
                    />
                    <button
                      type="button"
                      className="btn-remove"
                      onClick={() => removeFeature(index)}
                    >
                      <FaTrash />
                    </button>
                  </div>
                ))}
              </div>

              {/* Specifications Section */}
              <div className="form-section">
                <h3 className="section-title">
                  Specifications
                  <button type="button" className="btn-add" onClick={addSpecification}>
                    <FaPlus /> Add Specification
                  </button>
                </h3>
                {formData.specifications.map((spec, index) => (
                  <div key={index} className="specification-input">
                    <input
                      type="text"
                      value={spec.key}
                      placeholder="Specification name"
                      className="form-control"
                      onChange={(e) => updateSpecification(index, 'key', e.target.value)}
                    />
                    <input
                      type="text"
                      value={spec.value}
                      placeholder="Specification value"
                      className="form-control"
                      onChange={(e) => updateSpecification(index, 'value', e.target.value)}
                    />
                    <button
                      type="button"
                      className="btn-remove"
                      onClick={() => removeSpecification(index)}
                    >
                      <FaTrash />
                    </button>
                  </div>
                ))}
              </div>

              {/* Submit Button */}
              <div className="form-section">
                <button 
                  className="btn-create" 
                  onClick={handleCreate}
                  disabled={loading}
                >
                  {loading ? "Creating..." : "Create Car Listing"}
                </button>
              </div>
            </div>
          </div>
        </div>
      </div>
    </Layout>
  );
};

export default CreateProduct;
