import React, { useState, useEffect, useCallback, useMemo } from "react";
import Layout from "../../components/Layout/Layout";
import UserMenu from "../../components/Layout/UserMenu";
import axios from "axios";
import { useAuth } from "../../context/auth";
import moment from "moment";

const Orders = () => {
  const [orders, setOrders] = useState([]);
  const [auth] = useAuth();

  // Memoize the auth token to use in dependency arrays
  const authToken = useMemo(() => auth?.token, [auth]);

  const getOrders = useCallback(async () => {
    if (!authToken) return;

    try {
      const { data } = await axios.get("/api/v1/auth/orders", {
        headers: {
          Authorization: authToken,
        },
      });
      setOrders(data);
    } catch (error) {
      console.log(error);
    }
  }, [authToken]);

  useEffect(() => {
    if (authToken) {
      getOrders();
    }
  }, [authToken, getOrders]);

  return (
    <Layout title={"Your Orders"}>
      <div className="container-fluid m-3 p-3">
        <div className="row">
          <div className="col-md-3">
            <UserMenu />
          </div>
          <div className="col-md-9">
            <h1 className="text-center">Your Orders</h1>
            {orders?.map((order, index) => (
              <div className="border shadow mb-4" key={order._id}>
                <div className="table-responsive">
                  <table className="table">
                    <thead>
                      <tr>
                        <th scope="col">#</th>
                        <th scope="col">Status</th>
                        <th scope="col">Buyer</th>
                        <th scope="col">Date</th>
                        <th scope="col">Payment</th>
                        <th scope="col">Items</th>
                      </tr>
                    </thead>
                    <tbody>
                      <tr>
                        <td>{index + 1}</td>
                        <td>{order?.status}</td>
                        <td>{order?.buyer?.name}</td>
                        <td>{moment(order?.createdAt).fromNow()}</td>
                        <td>{order?.payment?.razorpay_payment_id}</td>
                        <td>{order?.products?.length}</td>
                      </tr>
                    </tbody>
                  </table>
                </div>
                <div className="container">
                  <div className="row m-2">
                    {order?.products?.map((product, i) => (
                      <div className="col-md-4" key={`${product._id}-${i}`}>
                        <div className="card">
                          <img
                            src={`/api/v1/product/product-photo/${product._id}`}
                            className="card-img-top"
                            alt={product.name}
                            style={{ height: "200px", objectFit: "cover" }}
                          />
                          <div className="card-body">
                            <h5 className="card-title">{product.name}</h5>
                            <p className="card-text">{product.description.substring(0, 30)}...</p>
                            <p className="card-text">Price: â‚¹{product.price}</p>
                          </div>
                        </div>
                      </div>
                    ))}
                  </div>
                </div>
              </div>
            ))}
          </div>
        </div>
      </div>
    </Layout>
  );
};

export default Orders;
