import React, { useState, useEffect, useCallback } from "react";
import Layout from "../../components/Layout/Layout";
import UserMenu from "../../components/Layout/UserMenu";
import { useAuth } from "../../context/auth";
import axios from "axios";
import moment from "moment";
import { toast } from "react-hot-toast";
import { useNavigate } from "react-router-dom";

const Dashboard = () => {
  const [auth] = useAuth();
  const [orders, setOrders] = useState([]);
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();

  // Memoize the auth token
  const authToken = auth?.token;

  // Format price for display
  const formatPrice = (price) => {
    return price.toLocaleString("en-IN", {
      style: "currency",
      currency: "INR",
    });
  };

  // Get user orders
  const getOrders = useCallback(async () => {
    if (!authToken) return;

    try {
      setLoading(true);
      const { data } = await axios.get("/api/v1/payment/orders", {
        headers: {
          Authorization: authToken,
        },
      });
      if (data?.success) {
        // Only show the 5 most recent orders
        const ordersWithProducts = data.orders.slice(0, 5).map(order => ({
          ...order,
          products: order.products.map(product => ({
            ...product,
            productId: product.product?._id || product.product
          }))
        }));
        setOrders(ordersWithProducts);
      }
      setLoading(false);
    } catch (error) {
      console.error("Error fetching orders:", error);
      toast.error("Error fetching your orders");
      setLoading(false);
    }
  }, [authToken]);

  useEffect(() => {
    getOrders();
  }, [getOrders]);

  return (
    <Layout title={"Dashboard - Car Rental"}>
      <div className="container-fluid m-3 p-3 dashboard">
        <div className="row">
          <div className="col-md-3">
            <UserMenu />
          </div>
          <div className="col-md-9">
            <div className="card shadow-sm p-3 mb-4">
              <h3 className="mb-4">Profile Information</h3>
              <div className="row">
                <div className="col-md-6">
                  <p><strong>Name:</strong> {auth?.user?.name}</p>
                  <p><strong>Email:</strong> {auth?.user?.email}</p>
                </div>
                <div className="col-md-6">
                  <p><strong>Phone:</strong> {auth?.user?.phone}</p>
                  <p><strong>Address:</strong> {auth?.user?.address}</p>
                </div>
              </div>
              <button 
                className="btn btn-primary mt-3"
                onClick={() => navigate("/dashboard/user/profile")}
              >
                Update Profile
              </button>
            </div>

            <div className="card shadow-sm p-3">
              <div className="d-flex justify-content-between align-items-center mb-4">
                <h3 className="mb-0">Recent Orders</h3>
                <button 
                  className="btn btn-outline-primary"
                  onClick={() => navigate("/dashboard/user/orders")}
                >
                  View All Orders
                </button>
              </div>

              {loading ? (
                <div className="text-center">
                  <div className="spinner-border" role="status">
                    <span className="visually-hidden">Loading...</span>
                  </div>
                </div>
              ) : orders?.length === 0 ? (
                <div className="text-center">
                  <p>No orders found. Start shopping to see your orders here!</p>
                </div>
              ) : (
                <div className="table-responsive">
                  <table className="table">
                    <thead>
                      <tr>
                        <th>Order ID</th>
                        <th>Date</th>
                        <th>Items</th>
                        <th>Amount</th>
                        <th>Status</th>
                      </tr>
                    </thead>
                    <tbody>
                      {orders.map((order) => (
                        <tr key={order._id} 
                            onClick={() => navigate("/dashboard/user/orders")}
                            style={{ cursor: "pointer" }}
                            className="hover-highlight">
                          <td>{order._id.substring(0, 8)}...</td>
                          <td>{moment(order.createdAt).format("MMM Do YY")}</td>
                          <td>
                            <div className="d-flex flex-wrap gap-2">
                              {order.products.map((item, index) => (
                                <img
                                  key={`${item.productId}-${index}`}
                                  src={`/api/v1/product/product-photo/${item.productId}`}
                                  alt={item.name}
                                  style={{ 
                                    width: "50px", 
                                    height: "50px", 
                                    objectFit: "cover",
                                    borderRadius: "4px"
                                  }}
                                />
                              ))}
                            </div>
                          </td>
                          <td>{formatPrice(order.amount)}</td>
                          <td>
                            <span className={`badge bg-${
                              order.status === "Delivered" ? "success" :
                              order.status === "Cancelled" ? "danger" :
                              "info"
                            }`}>
                              {order.status}
                            </span>
                          </td>
                        </tr>
                      ))}
                    </tbody>
                  </table>
                </div>
              )}
            </div>
          </div>
        </div>
      </div>
    </Layout>
  );
};

export default Dashboard;
