import React from "react";
import Layout from "../components/Layout/Layout";
import { useCart } from "../context/cart";
import { useAuth } from "../context/auth";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import "../styles/CartStyles.css";
import { toast } from "react-hot-toast";

const CartPage = () => {
  const [auth] = useAuth();
  const [cart, setCart] = useCart();
  const navigate = useNavigate();

  // Function to calculate total price
  const totalPrice = () => {
    try {
      const total = cart.reduce((total, item) => total + (Number(item.price) || 0), 0);
      return total.toLocaleString("en-IN", {
        style: "currency",
        currency: "INR",
      });
    } catch (error) {
      console.error("Error calculating total price:", error);
      return "0";
    }
  };

  // Function to remove an item from the cart
  const removeCartItem = (productId) => {
    try {
      const updatedCart = cart.filter((item) => item._id !== productId);
      setCart(updatedCart);
      localStorage.setItem("cart", JSON.stringify(updatedCart));
      toast.success("Item removed from cart");
    } catch (error) {
      console.error("Error removing item:", error);
      toast.error("Could not remove item");
    }
  };

  // Handle Razorpay payment
  const handlePayment = async () => {
    try {
      // Calculate total amount (in rupees first)
      const totalAmount = Math.round(cart.reduce((total, item) => total + (Number(item.price) || 0), 0));

      if (!totalAmount || totalAmount <= 0) {
        toast.error("Invalid cart amount");
        return;
      }

      // Validate cart items
      if (!cart.length) {
        toast.error("Your cart is empty");
        return;
      }

      // Validate user authentication
      if (!auth?.token || !auth?.user?._id) {
        toast.error("Please login to proceed");
        navigate("/login");
        return;
      }

      // Create an order on the server
      const orderData = {
        amount: totalAmount * 100, // Convert to paise for Razorpay
        currency: "INR",
        items: cart.map(item => ({
          _id: item._id,
          name: item.name,
          price: item.price
        }))
      };

      // First create order
      const { data: orderResponse } = await axios.post(
        "/api/v1/payment/create-order",
        orderData,
        {
          headers: {
            Authorization: auth.token,
            'Content-Type': 'application/json'
          },
        }
      );

      if (!orderResponse?.id) {
        toast.error("Could not create order. Please try again.");
        return;
      }

      // Load Razorpay script if not already loaded
      if (!window.Razorpay) {
        await loadRazorpayScript();
      }

      // Initialize Razorpay
      const options = {
        key: "rzp_test_OxAxRfdIt6ofSm", // Your test key
        amount: orderResponse.amount,
        currency: "INR",
        name: "Arouse Automation",
        description: "Car Rental Payment",
        order_id: orderResponse.id,
        prefill: {
          name: auth?.user?.name || "",
          email: auth?.user?.email || "",
          contact: auth?.user?.phone || "",
        },
        notes: {
          address: auth?.user?.address || ""
        },
        theme: {
          color: "#3399cc"
        },
        handler: async function(response) {
          try {
            const { razorpay_order_id, razorpay_payment_id, razorpay_signature } = response;

            if (!razorpay_order_id || !razorpay_payment_id || !razorpay_signature) {
              toast.error("Payment failed: Missing payment information");
              return;
            }

            // Verify payment
            const verificationData = {
              razorpay_order_id,
              razorpay_payment_id,
              razorpay_signature,
              orderId: orderResponse.id,
              amount: orderResponse.amount,
              cart: cart.map(item => ({
                _id: item._id,
                name: item.name,
                price: item.price
              })),
              buyer: auth.user._id,
              status: "Not Process"
            };

            const { data: verificationResponse } = await axios.post(
              "/api/v1/payment/verify-payment",
              verificationData,
              {
                headers: {
                  Authorization: auth.token,
                  'Content-Type': 'application/json'
                },
              }
            );

            if (verificationResponse.success) {
              setCart([]);
              localStorage.removeItem("cart");
              toast.success("Payment completed successfully");
              navigate("/dashboard/user/orders");
            } else {
              toast.error(verificationResponse.message || "Payment verification failed");
            }
          } catch (error) {
            console.error("Verification error:", error);
            toast.error(error.response?.data?.message || "Payment verification failed. Please contact support.");
          }
        },
        modal: {
          ondismiss: function() {
            toast.error("Payment cancelled by user");
          },
          escape: false,
          backdropclose: false
        }
      };

      const razorpay = new window.Razorpay(options);

      razorpay.on('payment.failed', function(response) {
        console.error("Payment failed:", response.error);
        toast.error(response.error.description || "Payment failed");
      });

      // Open Razorpay
      razorpay.open();

    } catch (error) {
      console.error("Payment error:", error);
      if (error.response?.data?.message) {
        toast.error(error.response.data.message);
      } else if (error.message) {
        toast.error(error.message);
      } else {
        toast.error("Error initiating payment. Please try again later.");
      }
    }
  };

  // Function to load Razorpay script
  const loadRazorpayScript = () => {
    return new Promise((resolve) => {
      const script = document.createElement('script');
      script.src = 'https://checkout.razorpay.com/v1/checkout.js';
      script.onload = () => {
        resolve(true);
      };
      script.onerror = () => {
        resolve(false);
        toast.error("Razorpay SDK failed to load. Please check your internet connection.");
      };
      document.body.appendChild(script);
    });
  };

  return (
    <Layout>
      <div className="container cart-page">
        <h2 className="text-center cart-title">
          <span role="img" aria-label="cart">üõí</span> Your Shopping Cart
        </h2>
        <p className="text-center cart-subtitle">
          {cart?.length
            ? `You have ${cart.length} item(s) in your cart.`
            : "Your cart is empty. Start shopping now!"}
        </p>

        <div className="row">
          <div className="col-lg-7">
            <div className="row row-cols-1 row-cols-md-2 g-4">
              {cart?.map((item, index) => (
                <div className="col" key={item._id || index}>
                  <div className="card cart-item-card">
                    <img
                      src={`/api/v1/product/product-photo/${item._id}`}
                      alt={item.name}
                      className="card-img-top cart-item-image"
                    />
                    <div className="card-body">
                      <h5 className="card-title">{item.name}</h5>
                      <p className="card-text">{item.description?.substring(0, 50)}...</p>
                      <h6 className="cart-price">Price: {item.price}</h6>
                      <button
                        className="btn btn-danger w-100"
                        onClick={() => removeCartItem(item._id)}
                      >
                        <span role="img" aria-label="remove">‚ùå</span> Remove
                      </button>
                    </div>
                  </div>
                </div>
              ))}
            </div>
          </div>

          <div className="col-lg-5">
            <div className="card shadow-lg p-4 cart-summary">
              <h3 className="text-center">
                <span role="img" aria-label="order">üõçÔ∏è</span> Order Summary
              </h3>
              <hr />
              <h4 className="text-center total-price">Total: {totalPrice()}</h4>

              {auth?.user?.address ? (
                <div className="address-section">
