import React, { useState, useEffect } from "react";
import Layout from "./../components/Layout/Layout";
import axios from "axios";
import { useParams, useNavigate } from "react-router-dom";
import "../styles/ProductDetailsStyles.css";
import toast from "react-hot-toast";
import { useCart } from "../context/cart";
import { FaCar,  FaArrowLeft, FaArrowRight } from "react-icons/fa";
import Slider from "react-slick";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";

const ProductDetails = () => {
    const params = useParams();
    const navigate = useNavigate();
    const [product, setProduct] = useState({});
    const [relatedProducts, setRelatedProducts] = useState([]);
    const [cart, setCart] = useCart();
    const [mainPhotoIndex, setMainPhotoIndex] = useState(0);
    const [loading, setLoading] = useState(true);
    const [photos, setPhotos] = useState([]);

    // Custom arrow components for the slider
    const NextArrow = ({ onClick }) => (
        <div className="slider-arrow next" onClick={onClick}>
            <FaArrowRight />
        </div>
    );

    const PrevArrow = ({ onClick }) => (
        <div className="slider-arrow prev" onClick={onClick}>
            <FaArrowLeft />
        </div>
    );

    // Slider settings
    const sliderSettings = {
        dots: true,
        infinite: true,
        speed: 500,
        slidesToShow: 1,
        slidesToScroll: 1,
        nextArrow: <NextArrow />,
        prevArrow: <PrevArrow />,
        beforeChange: (current, next) => setMainPhotoIndex(next)
    };

    // Get product details
    useEffect(() => {
        if (params.slug) {
            getProduct();
        }
        // eslint-disable-next-line
    }, [params.slug]);

    // Get product
    const getProduct = async () => {
        try {
            setLoading(true);
            const { data } = await axios.get(
                `/api/v1/product/get-product/${params.slug}`
            );
            setProduct(data?.product);
            getSimilarProduct(data?.product._id, data?.product.category._id);

            // Initialize photos array with main photo
            const photoUrls = [`/api/v1/product/product-photo/${data?.product._id}`];

            // Add additional photos if they exist
            if (data?.product?.additionalPhotos?.length > 0) {
                const additionalUrls = Array.from({ length: data.product.additionalPhotos.length }, 
                    (_, index) => `/api/v1/product/product-additional-photo/${data?.product._id}/${index}`
                );
                photoUrls.push(...additionalUrls);
            }

            setPhotos(photoUrls);
            setLoading(false);
        } catch (error) {
            console.log(error);
            setLoading(false);
        }
    };

    // Get similar products
    const getSimilarProduct = async (pid, cid) => {
        try {
            const { data } = await axios.get(
                `/api/v1/product/related-product/${pid}/${cid}`
            );
            setRelatedProducts(data?.products);
        } catch (error) {
            console.log(error);
        }
    };

    // Add to cart function
    const handleAddToCart = () => {
        const item = { ...product };
        setCart([...cart, item]);
        localStorage.setItem("cart", JSON.stringify([...cart, item]));
        toast.success("Car added to rental cart");
    };

    return (
        <Layout>
            <div className="container product-details">
                {loading ? (
                    <div className="text-center">Loading...</div>
                ) : (
                    <div className="row">
                        <div className="col-md-6">
                            <div className="product-image-gallery">
                                <div className="main-slider">
                                    <Slider {...sliderSettings}>
                                        {photos.map((photo, index) => (
                                            <div key={index} className="slider-image-container">
                                                <img
                                                    src={photo}
                                                    alt={`${product.name} view ${index + 1}`}
                                                    className="img-fluid"
                                                />
                                            </div>
                                        ))}
                                    </Slider>
                                </div>
                                <div className="thumbnail-grid">
                                    {photos.map((photo, index) => (
                                        <div
                                            key={index}
                                            className={`thumbnail ${mainPhotoIndex === index ? 'active' : ''}`}
                                            onClick={() => setMainPhotoIndex(index)}
                                        >
                                            <img
                                                src={photo}
                                                alt={`${product.name} thumbnail ${index + 1}`}
                                            />
                                        </div>
                                    ))}
                                </div>
                            </div>
                        </div>

                        <div className="col-md-6">
                            <div className="product-details-info">
                                <h1 className="product-title">{product.name}</h1>

                                <div className="product-info-item">
                                    <div className="info-label">Description</div>
                                    <div className="info-value">{product.description}</div>
                                </div>

                                <div className="product-price">
                                    {product?.price?.toLocaleString("en-IN", {
                                        style: "currency",
                                        currency: "INR",
                                    })}
                                    <span className="price-period">/day</span>
                                </div>

                                <div className="product-info-item">
                                    <div className="info-label">Category</div>
                                    <div className="info-value">{product?.category?.name}</div>
                                </div>

                                {/* Features Grid */}
                                <div className="features-grid">
                                    {product.features && product.features.map((feature, index) => (
                                        <div key={index} className="feature-item">
                                            <FaCar className="feature-icon" />
                                            <span>{feature}</span>
                                        </div>
                                    ))}
                                </div>

                                {/* Specifications Grid */}
                                {product.specifications && product.specifications.length > 0 && (
                                    <div className="specifications-grid">
                                        {product.specifications.map((spec, index) => (
                                            <div key={index} className="specification-item">
                                                <div className="spec-key">{spec.key}</div>
                                                <div className="spec-value">{spec.value}</div>
                                            </div>
                                        ))}
                                    </div>
                                )}

                                <button className="rent-button" onClick={handleAddToCart}>
                                    Add to Rental Cart
                                </button>
                            </div>
                        </div>
                    </div>
                )}

                {/* Similar Products Section */}
                <div className="similar-products">
                    <h2 className="similar-products-title">Similar Cars</h2>
                    {relatedProducts.length < 1 && (
                        <p className="text-center">No similar cars found</p>
                    )}
                    <div className="row">
                        {relatedProducts?.map((p) => (
                            <div className="col-md-4" key={p._id}>
                                <div className="similar-product-card">
                                    <img
                                        src={`/api/v1/product/product-photo/${p._id}`}
                                        className="similar-product-image"
                                        alt={p.name}
                                    />
                                    <div className="similar-product-body">
                                        <h5 className="similar-product-title">{p.name}</h5>
                                        <h6 className="similar-product-price">
                                            {p.price.toLocaleString("en-IN", {
                                                style: "currency",
                                                currency: "INR",
                                            })}
                                            <span className="price-period">/day</span>
                                        </h6>
                                        <p className="card-text">
                                            {p.description.substring(0, 60)}...
                                        </p>
                                        <div className="similar-product-buttons">
                                            <button
                                                className="similar-product-btn btn-details"
                                                onClick={() => navigate(`/product/${p.slug}`)}
                                            >
                                                View Details
                                            </button>
                                            <button
                                                className="similar-product-btn btn-rent"
                                                onClick={() => {
                                                    setCart([...cart, p]);
                                                    localStorage.setItem(
                                                        "cart",
                                                        JSON.stringify([...cart, p])
                                                    );
                                                    toast.success("Car added to rental cart");
                                                }}
                                            >
                                                Rent Now
                                            </button>
                                        </div>
                                    </div>
                                </div>
                            </div>
                        ))}
                    </div>
                </div>
            </div>
        </Layout>
    );
};

export default ProductDetails;
