import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { Checkbox, Radio } from "antd";
import { Prices } from "../components/Prices";
import { Carousel } from "react-responsive-carousel";
import { useCart } from "../context/cart";
import axios from "axios";
import toast from "react-hot-toast";
import Layout from "./../components/Layout/Layout";
import { AiOutlineReload } from "react-icons/ai";
import { FaSearch, FaCar, FaMoneyBillWave } from "react-icons/fa";
import "../styles/Homepage.css";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import "react-responsive-carousel/lib/styles/carousel.min.css";

const HomePage = () => {
  const navigate = useNavigate();
  const [cart, setCart] = useCart();
  const [products, setProducts] = useState([]);
  const [categories, setCategories] = useState([]);
  const [checked, setChecked] = useState([]);
  const [radio, setRadio] = useState([]);
  const [total, setTotal] = useState(0);
  const [page, setPage] = useState(1);
  const [loading, setLoading] = useState(false);

  // Fetch all categories
  const getAllCategory = async () => {
    try {
      const { data } = await axios.get("/api/v1/category/get-category");
      if (data?.success) {
        setCategories(data.category);
      }
    } catch (error) {
      console.error("Error fetching categories:", error);
    }
  };

  // Fetch total product count
  const getTotal = async () => {
    try {
      const { data } = await axios.get("/api/v1/product/product-count");
      setTotal(data?.total);
    } catch (error) {
      console.error("Error fetching total product count:", error);
    }
  };

  // Fetch products based on page
  const getAllProducts = async () => {
    try {
      setLoading(true);
      const { data } = await axios.get(`/api/v1/product/product-list/${page}`);
      setLoading(false);
      setProducts((prevProducts) => [...prevProducts, ...data.products]);
    } catch (error) {
      setLoading(false);
      console.error("Error fetching products:", error);
    }
  };

  // Fetch filtered products
  const filterProduct = async () => {
    try {
      const { data } = await axios.post("/api/v1/product/product-filters", {
        checked,
        radio,
      });
      setProducts(data?.products || []);
    } catch (error) {
      console.error("Error filtering products:", error);
    }
  };

  // Handle category filter
  const handleFilter = (value, id) => {
    const updatedChecked = value
      ? [...checked, id]
      : checked.filter((c) => c !== id);
    setChecked(updatedChecked);
  };

  useEffect(() => {
    if (page > 1) getAllProducts();
  }, [page]);

  useEffect(() => {
    getAllCategory();
    getTotal();
    getAllProducts();
  }, []);

  useEffect(() => {
    if (checked.length || radio.length) filterProduct();
  }, [checked, radio]);

  return (
    <Layout title={"Premium Car Rentals - Best Offers"}>
      <div className="homepage">
        {/* Hero Section */}
        <div className="banner-container">
          <Carousel
            showArrows={true}
            autoPlay={true}
            infiniteLoop={true}
            interval={5000}
            showThumbs={false}
            showStatus={false}
          >
            <div>
              <video autoPlay loop muted className="banner-video">
                <source src="/assest/car1.mp4" type="video/mp4" />
              </video>
              <div className="banner-overlay">
                <h1 className="banner-title">Premium Car Rental Service</h1>
                <p className="banner-subtitle">Experience Luxury and Comfort</p>
                <button className="banner-button" onClick={() => navigate("/categories")}>
                  Explore Our Fleet
                </button>
              </div>
            </div>
            <div>
              <img src="/assest/ban2.jpg" alt="Luxury Car" />
              <div className="banner-overlay">
                <h1 className="banner-title">Exclusive Collection</h1>
                <p className="banner-subtitle">Choose from Our Premium Selection</p>
                <button className="banner-button" onClick={() => navigate("/categories")}>
                  View Cars
                </button>
              </div>
            </div>
            <div>
              <img src="/assest/ban3.jpg" alt="Sports Car" />
              <div className="banner-overlay">
                <h1 className="banner-title">Drive Your Dreams</h1>
                <p className="banner-subtitle">Unforgettable Driving Experience</p>
                <button className="banner-button" onClick={() => navigate("/categories")}>
                  Book Now
                </button>
              </div>
            </div>
          </Carousel>
        </div>

        {/* Features Section */}
        <div className="features-section">
          <div className="container">
            <div className="row">
              <div className="col-md-4">
                <div className="feature-card">
                  <FaCar className="feature-icon" />
                  <h3>Premium Fleet</h3>
                  <p>Choose from our collection of luxury and sports cars</p>
                </div>
              </div>
              <div className="col-md-4">
                <div className="feature-card">
                  <FaMoneyBillWave className="feature-icon" />
                  <h3>Best Prices</h3>
                  <p>Competitive rates and flexible rental options</p>
                </div>
              </div>
              <div className="col-md-4">
                <div className="feature-card">
                  <FaSearch className="feature-icon" />
                  <h3>Easy Booking</h3>
                  <p>Simple and secure online booking process</p>
                </div>
              </div>
            </div>
          </div>
        </div>

        {/* Products Section */}
        <div className="container-fluid products-section">
          <div className="row">
            {/* Filters */}
            <div className="col-md-3">
              <div className="filters">
                <h4>Filter By Category</h4>
                <div className="d-flex flex-column">
                  {categories?.map((c) => (
                    <Checkbox
                      key={c._id}
                      onChange={(e) => handleFilter(e.target.checked, c._id)}
                    >
                      {c.name}
                    </Checkbox>
                  ))}
                </div>

                <h4>Filter By Price</h4>
                <div className="d-flex flex-column">
                  <Radio.Group onChange={(e) => setRadio(e.target.value)}>
                    {Prices?.map((p) => (
                      <div key={p._id}>
                        <Radio value={p.array}>{p.name}</Radio>
                      </div>
                    ))}
                  </Radio.Group>
                </div>

                <button
                  className="btn btn-danger"
                  onClick={() => {
                    setChecked([]);
                    setRadio([]);
                    window.location.reload();
                  }}
                >
                  Reset Filters
                </button>
              </div>
            </div>

            {/* Products */}
            <div className="col-md-9">
              <h2 className="section-title">Available Cars</h2>
              <div className="row">
                {products?.map((p) => (
                  <div className="col-md-6 col-lg-4" key={p._id}>
                    <div className="product-card">
                      <img
                        src={`/api/v1/product/product-photo/${p._id}`}
                        className="card-img-top"
                        alt={p.name}
                      />
                      <div className="card-body">
                        <div className="card-name-price">
                          <h5 className="card-title">{p.name}</h5>
                          <h5 className="card-price">
                            {p.price.toLocaleString("en-IN", {
                              style: "currency",
                              currency: "INR",
                            })}
                            <span className="price-period">/day</span>
                          </h5>
                        </div>
                        <p className="card-text">
                          {p.description.substring(0, 60)}...
                        </p>
                        <div className="card-buttons">
                          <button
                            className="btn btn-info"
                            onClick={() => navigate(`/product/${p.slug}`)}
                          >
                            View Details
                          </button>
                          <button
                            className="btn btn-dark"
                            onClick={() => {
                              setCart([...cart, p]);
                              localStorage.setItem(
                                "cart",
                                JSON.stringify([...cart, p])
                              );
                              toast.success("Added to rental cart");
                            }}
                          >
                            Rent Now
                          </button>
                        </div>
                      </div>
                    </div>
                  </div>
                ))}
              </div>

              {/* Load More */}
              {products && products.length < total && (
                <button
                  className="loadmore"
                  onClick={() => setPage(page + 1)}
                  disabled={loading}
                >
                  {loading ? (
                    "Loading..."
                  ) : (
                    <>
                      Load More <AiOutlineReload />
                    </>
                  )}
                </button>
              )}
            </div>
          </div>
        </div>
      </div>
    </Layout>
  );
};

export default HomePage;
