import React, { useState, useEffect, useCallback } from "react";
import Layout from "../../components/Layout/Layout";
import UserMenu from "../../components/Layout/UserMenu";
import { useAuth } from "../../context/auth";
import axios from "axios";
import moment from "moment";
import { toast } from "react-hot-toast";

const Dashboard = () => {
  const [auth] = useAuth();
  const [orders, setOrders] = useState([]);
  const [loading, setLoading] = useState(false);

  // Memoize the auth token
  const authToken = auth?.token;

  // Get user orders - using useCallback to memoize the function
  const getOrders = useCallback(async () => {
    if (!authToken) return;

    try {
      setLoading(true);
      const { data } = await axios.get("/api/v1/auth/orders", {
        headers: {
          Authorization: authToken,
        },
      });
      if (data?.success) {
        setOrders(data.orders);
      }
      setLoading(false);
    } catch (error) {
      console.error("Error fetching orders:", error);
      toast.error("Error fetching your orders");
      setLoading(false);
    }
  }, [authToken]); // Include authToken in dependencies

  useEffect(() => {
    getOrders();
  }, [getOrders]); // Only depend on getOrders which already has authToken dependency

  return (
    <Layout title={"Dashboard - Car Rental"}>
      <div className="container-fluid m-3 p-3 dashboard">
        <div className="row">
          <div className="col-md-3">
            <UserMenu />
          </div>
          <div className="col-md-9">
            <div className="card w-75 p-3 mb-4">
              <h3>User Profile</h3>
              <table className="table">
                <tbody>
                  <tr>
                    <td><strong>Name:</strong></td>
                    <td>{auth?.user?.name}</td>
                  </tr>
                  <tr>
                    <td><strong>Email:</strong></td>
                    <td>{auth?.user?.email}</td>
                  </tr>
                  <tr>
                    <td><strong>Address:</strong></td>
                    <td>{auth?.user?.address}</td>
                  </tr>
                  <tr>
                    <td><strong>Phone:</strong></td>
                    <td>{auth?.user?.phone}</td>
                  </tr>
                </tbody>
              </table>
            </div>

            <div className="orders-section">
              <h3>Your Recent Orders</h3>
              {loading ? (
                <div className="text-center">Loading...</div>
              ) : orders?.length === 0 ? (
                <div className="text-center">No orders found</div>
              ) : (
                orders?.map((order, index) => (
                  <div className="card mb-3" key={order._id}>
                    <div className="card-header">
                      <div className="d-flex justify-content-between">
                        <span>Order #{index + 1}</span>
                        <span>Status: <b>{order.status}</b></span>
                      </div>
                    </div>
                    <div className="card-body">
                      <div className="row">
                        <div className="col-md-6">
                          <p><strong>Order Date:</strong> {moment(order.createdAt).format("MMMM Do YYYY")}</p>
                          <p><strong>Payment ID:</strong> {order.payment?.razorpay_payment_id}</p>
                        </div>
                      </div>
                      <div className="products-list mt-3">
                        <h5>Rented Cars</h5>
                        {order.products?.map((p) => (
                          <div className="card mb-2" key={p._id}>
                            <div className="row g-0">
                              <div className="col-md-4">
                                <img
                                  src={`/api/v1/product/product-photo/${p._id}`}
                                  alt={p.name}
                                  className="img-fluid rounded-start"
                                  style={{ maxHeight: "150px", objectFit: "cover" }}
                                />
                              </div>
                              <div className="col-md-8">
                                <div className="card-body">
                                  <h5 className="card-title">{p.name}</h5>
                                  <p className="card-text">{p.description.substring(0, 100)}...</p>
                                  <p className="card-text">
                                    <small className="text-muted">
                                      Price: ₹{p.price}/day
                                    </small>
                                  </p>
                                </div>
                              </div>
                            </div>
                          </div>
                        ))}
                      </div>
                    </div>
                  </div>
                ))
              )}
            </div>
          </div>
        </div>
      </div>
    </Layout>
  );
};

export default Dashboard;
